import React, { useState } from 'react';
import styles from './Header.module.css'; 
import logophoto from '../../assets/images/logo.png';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Toggle menu visibility
  const toggleMenu = () => {
    setIsMenuOpen(prevState => !prevState);
  };

  // Close menu when a link is clicked
  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className={styles.header}>
      <div className={styles.container}>
        <a href="/Home" className={styles.logo}>
          <img src={logophoto} alt="Logo" className={styles.logoImage} /> 
        </a>
        <nav className={styles.nav}>
          <i
            className={`fas fa-bars ${styles.toggleMenu}`}
            onClick={toggleMenu} 
          ></i>
          <ul className={`${styles.navList} ${isMenuOpen ? styles.show : ''}`}>
            <li><a className={`${styles.navLink} ${styles.active}`} href="/Home" onClick={closeMenu}>Home</a></li>
            <li><a className={styles.navLink} href="/Services" onClick={closeMenu}>Services</a></li>
            <li><a className={styles.navLink} href="/Portfolio" onClick={closeMenu}>Portfolio</a></li>
            <li><a className={styles.navLink} href="/About" onClick={closeMenu}>About</a></li>
            <li><a className={styles.navLink} href="/Pricing" onClick={closeMenu}>Pricing</a></li>
            <li><a className={styles.navLink} href="/Contact" onClick={closeMenu}>Contact</a></li>
          </ul>
          <div className={styles.form}>
            <i className="fas fa-search"></i>
          </div>
        </nav>
      </div>
    </header>
  );
};

export default Header;
